ALTER TABLE public."PrestadorPJ" ENABLE ROW LEVEL SECURITY; DROP POLICY IF EXISTS "Allow prestadores to read their own data" ON public."PrestadorPJ"; DROP POLICY IF EXISTS "Allow public read for signup" ON public."PrestadorPJ"; CREATE POLICY "Allow prestadores to read their own data" ON public."PrestadorPJ" FOR SELECT TO authenticated USING (EXISTS (SELECT 1 FROM public.user_roles WHERE user_roles.id = auth.uid() AND user_roles.prestador_id = "PrestadorPJ".id));
