ALTER TABLE public.user_roles ENABLE ROW LEVEL SECURITY; DROP POLICY IF EXISTS "Allow insert during signup" ON public.user_roles; CREATE POLICY "Allow insert during signup" ON public.user_roles FOR INSERT TO public WITH CHECK (true); DROP POLICY IF EXISTS "Allow individual read access" ON public.user_roles; CREATE POLICY "Allow individual read access" ON public.user_roles FOR SELECT TO authenticated USING (auth.uid() = id);
